mapper.select:
SELECT COUNT(interest_id)
FROM interest
WHERE member_id = #{memberId}

(SELECT s.sharing_id AS id, s.member_id AS writerId, m.nickname AS writerNickname, s.title, s.created_at, 'SHARING' AS type FROM sharing s JOIN member m ON s.member_id = m.member_id WHERE s.member_id = #{memberId} AND s.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
) UNION ALL (SELECT q.question_id AS id, q.member_id AS writerId, m.nickname AS writerNickname, q.title, q.created_at, 'QUESTION' AS type FROM question q JOIN member m ON q.member_id = m.member_id WHERE q.member_id = #{memberId} AND q.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
) ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT s.sharing_id AS id, s.member_id AS writerId, m.nickname AS writerNickname, s.title, s.created_at, 'SHARING' AS type FROM sharing s JOIN member m ON s.member_id = m.member_id WHERE s.member_id = #{memberId} AND s.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY s.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT q.question_id AS id, q.member_id AS writerId, m.nickname AS writerNickname, q.title, q.created_at, 'QUESTION' AS type FROM question q JOIN member m ON q.member_id = m.member_id WHERE q.member_id = #{memberId} AND q.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY q.created_at DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE sharing
SET del_flag = NOW()
WHERE sharing_id IN

    #{id}

AND member_id = #{memberId}
AND del_flag IS NULL;

UPDATE question
SET del_flag = NOW()
WHERE question_id IN

    #{id}

AND member_id = #{memberId}
AND del_flag IS NULL;

mapper.select:
SELECT * FROM ( SELECT c.comment_id AS id, c.sharing_id AS target_id, c.writer_id, c.created_at, s.title AS title, m.nickname AS writer_nickname, 'SHARING' AS type FROM comment c JOIN sharing s ON c.sharing_id = s.sharing_id JOIN member m ON s.member_id = m.member_id WHERE c.writer_id = #{memberId} AND c.del_flag IS NULL AND s.title LIKE CONCAT('%', #{keyword}, '%')
UNION ALL
SELECT a.answer_id AS id, a.question_id AS target_id, a.writer_id, a.created_at, q.title AS title, m.nickname AS writer_nickname, 'QUESTION' AS type FROM answer a JOIN question q ON a.question_id = q.question_id JOIN member m ON q.member_id = m.member_id WHERE a.writer_id = #{memberId} AND a.del_flag IS NULL AND q.title LIKE CONCAT('%', #{keyword}, '%') ) t ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}
