mapper.select:
SELECT COUNT(interest_id)
FROM interest
WHERE member_id = #{memberId}

SELECT s.sharing_id, s.title, s.status, s.interest_num, s.created_at, s.review_flag, COALESCE(c.comment_count, 0) AS comment_count, ( SELECT file_url FROM image i WHERE i.target_type = 'SHARING' AND i.target_id = s.sharing_id AND i.del_flag IS NULL ORDER BY i.image_id ASC LIMIT 1 ) AS thumbnail FROM sharing s LEFT JOIN ( SELECT sharing_id, COUNT(*) AS comment_count FROM comment WHERE del_flag IS NULL GROUP BY sharing_id ) c ON c.sharing_id = s.sharing_id WHERE s.del_flag IS NULL
AND s.member_id = #{memberId}
AND s.target_member_id = #{memberId}
AND s.title LIKE CONCAT('%', #{keyword}, '%')
AND s.status = #{status}
ORDER BY s.created_at DESC LIMIT #{limit} OFFSET #{offset}
