mapper.select:
SELECT member_id, membername, nickname, phone, address, role, notice_enabled, del_flag
FROM member
WHERE member_id = #{memberId}

mapper.update:
UPDATE member
SET nickname = #{nickname},
    phone = #{phone},
    address = #{address}
WHERE member_id = #{memberId}

UPDATE member
SET notice_enabled = #{enabled}
WHERE member_id = #{memberId}
  AND del_flag IS NULL
