mapper.update:
UPDATE watering
SET check_flag =  NOW()
WHERE watering_id = #{wateringId}

UPDATE myplant
SET start_at = NULL, end_date = NULL, `interval` = NULL
WHERE myplant_id = #{myplantId}

mapper.select:
SELECT diary_id, myplant_id,activity,state,memo,created_at
FROM diary
WHERE diary_id = #{diaryId};

mapper.update:
UPDATE diary
SET activity = #{activity},
    state = #{state},
    memo = #{memo}
WHERE diary_id = #{diaryId};

