mapper.select:
SELECT COUNT(*)
FROM answer
WHERE answer_id = #{answerId}
  AND question_id = #{questionId}
  AND writer_id = #{writerId}
  AND del_flag IS NULL

mapper.update:
UPDATE answer
SET content = #{content},
    updated_at = NOW()
WHERE answer_id = #{answerId}
  AND question_id = #{questionId}
  AND writer_id = #{writerId}
  AND del_flag IS NULL

UPDATE answer
SET del_flag = NOW()
WHERE answer_id = #{answerId}
  AND question_id = #{questionId}
  AND writer_id = #{writerId}
  AND del_flag IS NULL
